import React from "react";
import styled from "styled-components";
import {motion} from 'framer-motion';


const BasicsOfMotion = () => {
  return <Container>
    <motion.div
    initial={{
      rotate: "0deg",
    }}
    animate = {{
      rotate: "180deg",}}
    
    transition = {{
      duration: 1,
      type: "spring",
    }}
    ></motion.div>
  </Container>;
};

const Container = styled.div`
  display: grid;
  place-content: center;
  height: 100vh;
  gap: 0.8rem;
  div{
    width: 150px;
    height: 150px;
    background: black;
  }
`;


export default BasicsOfMotion;
